import { useState } from "react";
import "./App.css";

function App() {
  const correctAnswer = "Burger";

  const foodItems = [
    { name: "Pizza", img: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQHr4FiIslJFTrLxaoRgE1djF3rTfgBDS-kOw&s" },
    { name: "Sushi", img: "data:image/jpeg;base64," },
    { name: "Burger", img: "data:image/jpeg;base64," }, // correct
    { name: "Fries", img: "data:image/jpeg;base64," },
    { name: "Taco", img: "data:image/jpeg;base64," },
    { name: "Hotdog", img: "data:image/jpeg;base64," },
    { name: "Pasta", img: "https://www.shutterstock.com/image-photo/classic-italian-pasta-penne-alla-260nw-2556000201.jpg" },
    { name: "Donut", img: "data:image/jpeg;base64," },
    { name: "Bacon", img: "data:image/jpeg;base64," },
  ];

  const [revealed, setRevealed] = useState(Array(foodItems.length).fill(false));
  const [attempts, setAttempts] = useState(3);
  const [message, setMessage] = useState("Guess Me!");
  const [gameOver, setGameOver] = useState(false);

  const handleClick = (index) => {
    if (gameOver || revealed[index]) return;

    const newRevealed = [...revealed];
    newRevealed[index] = true;
    setRevealed(newRevealed);

    const chosenFood = foodItems[index].name;
    if (chosenFood === correctAnswer) {
      setMessage(" You guessed it! It's a Burger!");
      setGameOver(true);
    } else {
      const newAttempts = attempts - 1;
      setAttempts(newAttempts);
      if (newAttempts === 0) {
        setMessage(" Out of attempts! Try again.");
        setGameOver(true);
      } else {
        setMessage(`Wrong! Attempts left: ${newAttempts}`);
      }
    }
  };

  const resetGame = () => {
    setRevealed(Array(foodItems.length).fill(false));
    setAttempts(3);
    setMessage("Guess Me!");
    setGameOver(false);
  };

  return (
    <div className="game">
      <h1> Guess Me!</h1>
      <p className="status">{message}</p>
      <div className="grid">
        {foodItems.map((food, index) => (
          <div
            key={index}
            className="card"
            onClick={() => handleClick(index)}
          >
            {revealed[index] ? (
              <img src={food.img} alt={food.name} />
            ) : (
              <span className="question"></span>
            )}
          </div>
        ))}
      </div>
      {gameOver && (
        <button className="reset-btn" onClick={resetGame}>
           Try Again
        </button>
      )}
    </div>
  );
}

export default App;


